
function speed_eval_config(this_block)

  % Revision History:
  %
  %   10-Mar-2013  (22:03 hours):
  %     Original code was machine generated by Xilinx's System Generator after parsing
  %     E:\Matlab\hdlsrc\speed_eval.vhd
  %
  %

  this_block.setTopLevelLanguage('VHDL');

  this_block.setEntityName('speed_eval');

  % System Generator has to assume that your entity  has a combinational feed through; 
  %   if it  doesn't, then comment out the following line:
  this_block.tagAsCombinational;

  this_block.addSimulinkInport('clk');
  this_block.addSimulinkInport('reset');
  this_block.addSimulinkInport('clk_enable');
  this_block.addSimulinkInport('ref_speed');
  this_block.addSimulinkInport('speed_cnt');

  this_block.addSimulinkOutport('ce_out');
  this_block.addSimulinkOutport('error_rsvd');
  this_block.addSimulinkOutport('speed');

  ce_out_port = this_block.port('ce_out');
  ce_out_port.setType('UFix_1_0');
  ce_out_port.useHDLVector(false);
  error_rsvd_port = this_block.port('error_rsvd');
  error_rsvd_port.setType('UFix_32_0');
  speed_port = this_block.port('speed');
  speed_port.setType('UFix_32_0');

  % -----------------------------
  if (this_block.inputTypesKnown)
    % do input type checking, dynamic output type and generic setup in this code block.

    if (this_block.port('clk').width ~= 1);
      this_block.setError('Input data type for port "clk" must have width=1.');
    end

    this_block.port('clk').useHDLVector(false);

    if (this_block.port('reset').width ~= 1);
      this_block.setError('Input data type for port "reset" must have width=1.');
    end

    this_block.port('reset').useHDLVector(false);

    if (this_block.port('clk_enable').width ~= 1);
      this_block.setError('Input data type for port "clk_enable" must have width=1.');
    end

    this_block.port('clk_enable').useHDLVector(false);

    if (this_block.port('ref_speed').width ~= 32);
      this_block.setError('Input data type for port "ref_speed" must have width=32.');
    end

    if (this_block.port('speed_cnt').width ~= 32);
      this_block.setError('Input data type for port "speed_cnt" must have width=32.');
    end

  end  % if(inputTypesKnown)
  % -----------------------------

  % System Generator found no apparent clock signals in the HDL, assuming combinational logic.
  % -----------------------------
   if (this_block.inputRatesKnown)
     inputRates = this_block.inputRates; 
     uniqueInputRates = unique(inputRates); 
     outputRate = uniqueInputRates(1);
     for i = 2:length(uniqueInputRates)
       if (uniqueInputRates(i) ~= Inf)
         outputRate = gcd(outputRate,uniqueInputRates(i));
       end
     end  % for(i)
     for i = 1:this_block.numSimulinkOutports 
       this_block.outport(i).setRate(outputRate); 
     end  % for(i)
   end  % if(inputRatesKnown)
  % -----------------------------

    % (!) Set the inout port rate to be the same as the first input 
    %     rate. Change the following code if this is untrue.
    uniqueInputRates = unique(this_block.getInputRates);


  % Add addtional source files as needed.
  %  |-------------
  %  | Add files in the order in which they should be compiled.
  %  | If two files "a.vhd" and "b.vhd" contain the entities
  %  | entity_a and entity_b, and entity_a contains a
  %  | component of type entity_b, the correct sequence of
  %  | addFile() calls would be:
  %  |    this_block.addFile('b.vhd');
  %  |    this_block.addFile('a.vhd');
  %  |-------------

  %    this_block.addFile('');
  %    this_block.addFile('');
  this_block.addFile('hdlsrc/speed_eval.vhd');

return;


